mapper.update:
    UPDATE tmaster SET 
	          appid=#{appid}, lvl=#{lvl}, desc1=#{desc1}, cmpCode=#{cmpCode}, tdate=#{tdate}, endDate=#{endDate}, 
            tdir=#{tdir}, tuser=#{tuser}, thost=#{thost}, tport=#{tport}, tenv=#{tenv},pro=#{pro}
       WHERE CODE = #{code}

mapper.select:
select group_concat(t.tcode) tcode, t.svcid, s.svckor , sum(tcnt) tcnt, round(avg(t.avgt),3) avgt,
     round(avg(t.stdv),3) stdv,sum(scnt) scnt 
    , sum(fcnt) fcnt , s.cumcnt 
    from   tsvcsum t join tservice s on (t.svcid = s.svcid and t.appid = s.appid) 
          join (select code, lvl from tmaster ) m on (t.tcode = m.code) 
    WHERE tcnt != 0 
      and t.tcode = #{tcode} 
      and s.task = #{task} 
      and lvl = #{lvl}  
     group by t.svcid ORDER BY t.svcid

mapper.update:
UPDATE taqtuser 
SET pass1 = password('${npass}') 
WHERE usrid = #{usrid}

update.foreach:
UPDATE tservice 
SET appid=#{item.appid}, svcid='${item.svcid}', svckor='${item.svckor}',
    svceng='${item.svceng}', task=#{item.task}, manager='${item.manager}',svckind=#{item.svckind} 
WHERE pkey = ${item.pkey}
